import React from 'react';

const QuestionPage = ({ question, optionA, optionB, displayNextQuestion, currentQuestionIndex, totalQuestions, svgUrls, backgroundImage }) => {
  const parentContainerClass = currentQuestionIndex > 0 && currentQuestionIndex <= totalQuestions ? 'container--question-page' : 'container';

  return (
    <section className="question-page">
      <div className={parentContainerClass} style={{ 
        backgroundImage: `url('${backgroundImage}')`,
        backgroundSize: 'contain',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat'
      }}>
        <div className="question-content">
          <h2>{question}</h2>
        </div>
        <div className="options">
          <button onClick={() => displayNextQuestion('A')}><span>{optionA}</span></button>
          <button onClick={() => displayNextQuestion('B')}><span>{optionB}</span></button>
        </div>
      </div>
    </section>
  );
};

export default QuestionPage;
