import React from 'react';

const StartPage = ({ displayNextQuestion, svgUrls, backgroundImage }) => {
  return (
    <section className="start-page" style={{ 
      backgroundImage: `url('${backgroundImage}')`,
      backgroundSize: 'contain',
      backgroundPosition: 'center',
      backgroundRepeat: 'no-repeat',
      padding: '20px', // Padding around the section
      position: 'relative', // Ensure the section is relatively positioned
      minHeight: '100vh', // Ensure the section has at least the viewport height
      display: 'flex', // Use flexbox to center content vertically
      flexDirection: 'column', // Align items vertically
      justifyContent: 'center', // Center items vertically
    }}>
      <div style={{ position: 'absolute', bottom: '60px', left: '50%', transform: 'translateX(-50%)' }}>
        <img src={svgUrls.lark} alt="Lark Logo" style={{ width: '150px' }} />
      </div>
      <h1 style={{ marginBottom: '20px', fontSize: '48px' }}>Welcome to Larkâ€™s Workplace Personality Quiz!</h1>
      <h2 style={{ marginBottom: '20px' }}>Embark on a fun journey to discover your unique work style and preferences! Get ready to dive into exciting scenarios and uncover insights that'll help you thrive in the workplace!</h2>     
      <button id="start-btn" style={{ marginBottom: '20px' }} onClick={() => displayNextQuestion(null)}><span>Start Quiz</span></button>
    </section>
  );
};

export default StartPage;
