import React from 'react';

const QuestionPage = ({ question, optionA, optionB, displayNextQuestion, currentQuestionIndex, totalQuestions, svgUrls, backgroundImage, style }) => {
  const questionPageStyle = {
    backgroundImage: `url('${backgroundImage}')`,
    backgroundSize: 'contain',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    textAlign: 'center',
    justifyContent: 'flex-start', // Align content to the top
  };

  return (
    <section className="question-page" style={questionPageStyle}>
      <div className="question-content">
        <h2>{question}</h2>
      </div>
      <div className="options">
        <button onClick={() => displayNextQuestion('A')}><span>{optionA}</span></button>
        <button onClick={() => displayNextQuestion('B')}><span>{optionB}</span></button>
      </div>
    </section>
  );
};

export default QuestionPage;
