import React from 'react';
import './App.css'; // Import ResultPage styles

const ResultPage = ({ mbtiType, restartQuiz }) => {
  // Function to copy the URL to the clipboard
  const copyToClipboard = async () => {
    try {
      await navigator.clipboard.writeText(window.location.href);
      alert('URL copied to clipboard!');
    } catch (error) {
      console.error('Error copying to clipboard:', error);
    }
  };

  // Function to share via Telegram
  const shareViaTelegram = () => {
    const message = encodeURIComponent(`Check out my workplace personality quiz result! ${window.location.href}`);
    const telegramUrl = `https://t.me/share/url?url=${message}`;
    window.open(telegramUrl, '_blank');
  };

  // Function to share via WhatsApp
  const shareViaWhatsApp = () => {
    const message = encodeURIComponent(`Check out my workplace personality quiz result! ${window.location.href}`);
    const whatsappUrl = `https://api.whatsapp.com/send?text=${message}`;
    window.open(whatsappUrl, '_blank');
  };

  // Function to download the image
  const downloadImage = () => {
    const imageUrl = `/images/${mbtiType}.png`;
    const link = document.createElement('a');
    link.href = imageUrl;
    link.download = `${mbtiType}_result.png`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  // Render the result content only if mbtiType has a value
  if (!mbtiType) return null; // Don't render anything if mbtiType is empty

  return (
    <section className="results-page">
      <div className="result-content">
        <img src={`/images/${mbtiType}.png`} alt={mbtiType} className="mbti-image" />
        <div className="icon-container">
          <button onClick={copyToClipboard} className="circular-icon"><span>Share Link</span></button>
          <button onClick={shareViaTelegram} className="circular-icon"><span>Telegram</span></button>
          <button onClick={shareViaWhatsApp} className="circular-icon"><span>WhatsApp</span></button>
          <button onClick={downloadImage} className="circular-icon"><span>Download</span></button>
          <button onClick={restartQuiz} className="circular-icon"><span>Restart</span></button>
        </div>
      </div>
    </section>
  );
};

export default ResultPage;
