import React, { useState } from 'react';
import './App.css';

function App() {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);

  const questions = [
    {
      question: "Q1/8: You get magically teleported into a new company and wake up to your team looking over you in excitement and a gleam in their eyes. Your first thought:",
      optionA: "What the?! Where am I? Who are these people?",
      optionB: "I've never had anyone look at me with such eagerness."
    },
    {
      question: "Q2/8: As your colleagues help you up, you notice a life-sized dog being introduced as the company's mascot. Your initial reaction:",
      optionA: "Woah! That's adorable, can I pet it?",
      optionB: "Wait, is that a real-life human-sized dog with human intellect? This feels surreal."
    }
    // Add more questions here
  ];

  const displayNextQuestion = () => {
    setCurrentQuestionIndex(prevIndex => prevIndex + 1);
  };

  const startQuiz = () => {
    setCurrentQuestionIndex(1); // Start quiz by setting the current question index to 1
  };

  const handleOptionClick = () => {
    // Navigate to the next question when an option is clicked
    displayNextQuestion();
  };

  return (
    <div className="container">
      <header>
        <h1>Personality Quiz</h1>
      </header>
      <main>
        {/* Start Page */}
        <section id="start-page" className="question-container" style={{ display: currentQuestionIndex === 0 ? 'block' : 'none' }}>
          <h2>Welcome to the Personality Quiz!</h2>
          <img src="images/start.gif" alt="Start Image" />
          <p>Click the button below to start.</p>
          <button id="start-btn" onClick={startQuiz}>Start Quiz</button>
        </section>

        {/* Question Pages */}
        {questions.map((question, index) => (
          <section key={index} className="question-container" style={{ display: currentQuestionIndex === index + 1 ? 'block' : 'none' }}>
            <h2>{question.question}</h2>
            <div className="options">
              <button onClick={handleOptionClick}>{question.optionA}</button>
              <button onClick={handleOptionClick}>{question.optionB}</button>
            </div>
          </section>
        ))}

        {/* End of Quiz */}
        {currentQuestionIndex > questions.length && (
          <section className="question-container">
            <h2>End of Quiz!</h2>
          </section>
        )}
      </main>
      <footer>
        <p>&copy; 2024 Personality Quiz</p>
      </footer>
    </div>
  );
}

export default App;
