import React from 'react';

const QuestionPage = ({ question, optionA, optionB, displayNextQuestion, currentQuestionIndex, totalQuestions, svgUrls, backgroundImage }) => {
    // Find the font color range for the current question index
    const fontColorRange = fontColorRanges.find(range => currentQuestionIndex >= range.start && currentQuestionIndex <= range.end);
    const fontColor = fontColorRange ? fontColorRange.color : '#000'; // Default to black if no matching range is found

  return (
    <section className="question-page">
      <div className="question-content" style={{ 
        backgroundImage: `url('${backgroundImage}')`,
        backgroundSize: 'contain',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat'
      }}>
        <h2 style={{ color: fontColor }}>{question}</h2>
      </div>
      <div className="options">
        <button onClick={() => displayNextQuestion('A')}><span>{optionA}</span></button>
        <button onClick={() => displayNextQuestion('B')}><span>{optionB}</span></button>
      </div>
    </section>
  );
};

export default QuestionPage;
