import React, { useState, useEffect } from 'react';
import './App.css';
import LoaderPage from './LoaderPage';
import StartPage from './StartPage'; // Import StartPage component
import QuestionPage from './QuestionPage'; // Import QuestionPage component
import ResultPage from './ResultPage'; // Import ResultPage component
import svgUrls from './svgurl'; // Import SVG URLs

function App() {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [userSelections, setUserSelections] = useState([]);
  const [mbtiType, setMbtiType] = useState('');
  const [showLoader, setShowLoader] = useState(false);
  const [backgroundImage, setBackgroundImage] = useState('url("")');
  const [isQuestionPage, setIsQuestionPage] = useState(false); // State to track if it's a question page

  const questions = [
    {
      question: "Q1/12: You get magically teleported into a new company and wake up to your team looking over you in excitement and a gleam in their eyes. Your first thought:",
      optionA: "What the?! Where am I? Who are these people?",
      optionB: "I've never had anyone look at me with such eagerness.",
    },
    {
      question: "Q2/12: As your colleagues help you up, you notice a life-sized dog being introduced as the company's mascot. Your initial reaction:",
      optionA: "Woah! That's adorable, can I pet it?",
      optionB: "Wait, is that a real-life human-sized dog with human intellect? This feels surreal.",
    },
    {
      question: "Q3/12: You're presented with a complex problem to solve. Your approach:",
      optionA: "I'll analyze the data and crunch the numbers to find a logical solution.",
      optionB: "I'll consider how the problem affects people and their emotions before making a decision.",
    },
    {
      question: "Q4/12: A project deadline is approaching, but you discover a flaw in your initial plan. Your response:",
      optionA: "I'll stick to the plan and try to make it work, even with the flaw.",
      optionB: "I'll adjust the plan to fix the flaw and ensure a better outcome.",
    },
    {
      question: "Q5/12: You find yourself in a crowded room with people you don't know. Your instinct:",
      optionA: "I'll try to blend in and observe the dynamics before engaging with anyone.",
      optionB: "I'll dive right in and start conversations with whoever seems interesting.",
    },
    {
      question: "Q6/12: You're planning a trip to a new destination. Your priority:",
      optionA: "I'll research all the attractions and plan a detailed itinerary.",
      optionB: "I'll go with the flow and explore whatever catches my interest.",
    },
    {
      question: "Q7/12: You're leading a team on a challenging project. Your leadership style:",
      optionA: "I'll focus on setting clear goals and ensuring everyone follows the plan.",
      optionB: "I'll empower my team to innovate and find creative solutions.",
    },
    {
      question: "Q8/12: You have a busy day ahead with multiple tasks to complete. Your approach:",
      optionA: "I'll prioritize my tasks and tackle them one by one in an organized manner.",
      optionB: "I'll go with the flow and handle tasks as they come up, adjusting my plans as needed.",
    },
    {
      question: "Q9/12: You're invited to a social event with unfamiliar faces. Your reaction:",
      optionA: "I'll feel energized by the opportunity to meet new people and make connections.",
      optionB: "I'll feel more comfortable sticking to familiar surroundings and people I know.",
    },
    {
      question: "Q10/12: You're faced with a problem that has no clear solution. Your approach:",
      optionA: "I'll explore various possibilities and brainstorm creative solutions.",
      optionB: "I'll rely on established methods and proven strategies to find a solution.",
    },
    {
      question: "Q11/12: You receive constructive criticism on your work. Your response:",
      optionA: "I'll appreciate the feedback and use it to improve my future efforts.",
      optionB: "I'll defend my work and explain why I think it's already good as it is.",
    },
    {
      question: "Q12/12: You're planning a vacation. Your focus:",
      optionA: "I'll plan every detail to ensure a well-organized and efficient trip.",
      optionB: "I'll leave room for spontaneity and unexpected adventures along the way.",
    },
  ];

  const displayNextQuestion = (selectedOption) => {
    setUserSelections(prevSelections => [...prevSelections, selectedOption]);
    setCurrentQuestionIndex(prevIndex => prevIndex + 1);
  };

  const restartQuiz = () => {
    setCurrentQuestionIndex(0);
    setUserSelections([]);
    console.log("Quiz restarted. User Selections cleared.");
  };

  const calculateMBTI = () => {
    // Calculation logic...
  };

  const handleQuizCompletion = () => {
    // Handling quiz completion...
  };

  useEffect(() => {
    // Effect for handling quiz completion...
  }, [currentQuestionIndex, questions.length]);

  useEffect(() => {
    // Effect for setting background image...
  }, [currentQuestionIndex, questions.length, svgUrls]);

  useEffect(() => {
    // Effect for updating isQuestionPage state
    setIsQuestionPage(currentQuestionIndex > 0 && currentQuestionIndex <= questions.length);
  }, [currentQuestionIndex, questions.length]);

  return (
    <div className={`container ${isQuestionPage ? 'question-page-container' : ''}`} style={{backgroundImage}}>
      <main>
        {showLoader && <LoaderPage />}

        {/* Start Page */}
        {currentQuestionIndex === 0 && !showLoader && (
          <StartPage 
            displayNextQuestion={displayNextQuestion} 
            svgUrls={svgUrls} 
            backgroundImage={backgroundImage} 
          />
        )}

        {/* Question Pages */}
        {currentQuestionIndex > 0 && currentQuestionIndex <= questions.length && !showLoader && (
          <QuestionPage 
            question={questions[currentQuestionIndex - 1].question} 
            optionA={questions[currentQuestionIndex - 1].optionA} 
            optionB={questions[currentQuestionIndex - 1].optionB} 
            displayNextQuestion={displayNextQuestion} 
            currentQuestionIndex={currentQuestionIndex} 
            totalQuestions={questions.length} 
            svgUrls={svgUrls} 
            backgroundImage={backgroundImage} 
          />
        )}

        {/* Result Page */}
        {currentQuestionIndex > questions.length && !showLoader && (
          <ResultPage 
            restartQuiz={restartQuiz} 
            calculateMBTI={calculateMBTI} 
            userSelections={userSelections} 
          />
        )}
      </main>
      <footer>
        {/* Footer content */}
      </footer>
    </div>
  );
}

export default App;
