import svgUrls from './svgurl'; // Import SVG URLs
import React, { useState } from 'react';

const ResultPage = ({ mbtiType, restartQuiz }) => {
    const [copied, setCopied] = useState(false);

    // Function to share the quiz link
    const copyToClipboard = async () => {
        try {
            await navigator.clipboard.writeText(window.location.href);
            setCopied(true);
        } catch (error) {
            console.error('Error copying to clipboard:', error);
        }
    };

    // Function to share via Telegram
    const shareViaTelegram = () => {
        const message = encodeURIComponent(`Check out my workplace personality quiz result! ${window.location.href}`);
        const telegramUrl = `https://t.me/share/url?url=${message}`;
        window.open(telegramUrl, '_blank');
    };

    // Function to share via WhatsApp
    const shareViaWhatsApp = () => {
        const message = encodeURIComponent(`Check out my workplace personality quiz result! ${window.location.href}`);
        const whatsappUrl = `https://api.whatsapp.com/send?text=${message}`;
        window.open(whatsappUrl, '_blank');
    };

    // Function to download the image
    const downloadImage = () => {
        const imageUrl = `/images/${mbtiType}.png`;
        const link = document.createElement('a');
        link.href = imageUrl;
        link.download = `${mbtiType}_result.png`;
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
    };

    // Render the result content only if mbtiType has a value
    if (!mbtiType) return null; // Don't render anything if mbtiType is empty

    return (
        <section className="results-page">
            <div className="result-content" style={{ display: 'flex', flexDirection: 'column' }}>

                {/* Div for the image result */}
                <div className="image-result" style={{ marginBottom: '10px' }}>
                    <img src={`/images/${mbtiType}.png`} alt={mbtiType} className="mbti-image" />
                </div>

                {/* Heading */}
                <h2 style={{ marginBottom: '10px', fontSize:"15px"}}>Share the quiz with your friends and see how you guys hit it off in the workplace!</h2>

                {/* Div for each long button */}
                <div className="long-buttons" style={{ marginBottom: '10px' }}>
                    <button onClick={restartQuiz}><span style={{ fontWeight: '600' }}>Restart Quiz</span></button>
                    <button onClick={copyToClipboard}><span style={{ fontWeight: '600' }}>{copied ? 'Copied!' : 'Copy Quiz Link to Clipboard'}</span></button>
                </div>

                {/* Div for the 3 circular buttons */}
                <div className="circular-buttons" style={{ marginBottom: '10px', display: 'flex', justifyContent: 'center'}}>
                    <div className="circular-icon telegram" style={{ marginRight: '10px' }} onClick={shareViaTelegram}>
                        <img src={svgUrls.telegram} alt="Telegram" />
                    </div>
                    <div className="circular-icon whatsapp" style={{ marginRight: '10px' }} onClick={shareViaWhatsApp}>
                        <img src={svgUrls.whatsapp} alt="WhatsApp" />
                    </div>
                    <div className="circular-icon download" onClick={downloadImage}>
                        <img src={svgUrls.download} alt="Download" />
                    </div>
                </div>

                {/* Powered by Lark */}
                <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', color: 'white', fontSize: '12px', marginTop: '10px' }}>
                    <span>Powered by </span>
                    <img src={svgUrls.lark} alt="Lark Logo" style={{ width: '20px', padding: '0 5px' }} />
                </div>

                {/* Copyright text */}
                <p style={{ color: 'white', textAlign: 'center', fontSize: "12px" }}>Â© 2024 Lark Technologies Pte. Ltd.</p>
                </div>
            </section>
    );
};

export default ResultPage;
