import React from 'react';
import svgUrls from './svgurl'; // Import SVG URLs

const ResultPage = ({ mbtiType, restartQuiz }) => {
    // Function to share the quiz link
// Function to share the quiz link
const shareQuizLink = async () => {
    // Hardcoded HTTPS URL for testing
    const hardcodedUrl = "https://example.com"; // Replace "example.com" with your actual URL

    // Check if the Web Share API is supported
    if (navigator.share) {
        try {
            // Share the quiz link using the Web Share API
            await navigator.share({
                title: 'Workplace Personality Quiz Result',
                text: 'Check out my workplace personality quiz result!',
                url: hardcodedUrl
            });
            console.log('Quiz link shared successfully!');
        } catch (error) {
            console.error('Error sharing quiz link:', error);
            // Handle errors here, such as displaying a message to the user
        }
    } else {
        // Fallback mechanism for browsers that do not support the Web Share API
        alert('Sharing is not supported in your browser. You can copy the link manually to share.');
        // Provide instructions or UI for manually copying the link
    }
};


  // Function to share via Telegram
  const shareViaTelegram = () => {
    const message = encodeURIComponent(`Check out my workplace personality quiz result! ${window.location.href}`);
    const telegramUrl = `https://t.me/share/url?url=${message}`;
    window.open(telegramUrl, '_blank');
  };

  // Function to share via WhatsApp
  const shareViaWhatsApp = () => {
    const message = encodeURIComponent(`Check out my workplace personality quiz result! ${window.location.href}`);
    const whatsappUrl = `https://api.whatsapp.com/send?text=${message}`;
    window.open(whatsappUrl, '_blank');
  };

  // Function to download the image
  const downloadImage = () => {
    const imageUrl = `/images/${mbtiType}.png`;
    const link = document.createElement('a');
    link.href = imageUrl;
    link.download = `${mbtiType}_result.png`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  // Render the result content only if mbtiType has a value
  if (!mbtiType) return null; // Don't render anything if mbtiType is empty

  return (
    <section className="results-page">
      <div className="result-content">
        <img src={`/images/${mbtiType}.png`} alt={mbtiType} className="mbti-image" />

        <button onClick={restartQuiz}><span>Restart Quiz</span></button>
        <button onClick={shareQuizLink}><span>Share Quiz Link</span></button>

        <div className="circular-icon telegram" onClick={shareViaTelegram}>
          <img src={svgUrls.telegram} alt="Telegram" />
        </div>
        <div className="circular-icon whatsapp" onClick={shareViaWhatsApp}>
          <img src={svgUrls.whatsapp} alt="WhatsApp" />
        </div>
        <div className="circular-icon download" onClick={downloadImage}>
          <img src={svgUrls.download} alt="Download" />
        </div>
      </div>
    </section>
  );

};

export default ResultPage;
