import React, { useState } from 'react';
import './App.css';

function App() {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);

  const questions = [
    {
      question: "Q1/8: You get magically teleported into a new company and wake up to your team looking over you in excitement and a gleam in their eyes. Your first thought:",
      optionA: "What the?! Where am I? Who are these people?",
      optionB: "I've never had anyone look at me with such eagerness."
    },
    {
      question: "Q2/8: As your colleagues help you up, you notice a life-sized dog being introduced as the company's mascot. Your initial reaction:",
      optionA: "Woah! That's adorable, can I pet it?",
      optionB: "Wait, is that a real-life human-sized dog with human intellect? This feels surreal."
    },
    {
      question: "Question 3: Lorem ipsum dolor sit amet?",
      optionA: "Option A",
      optionB: "Option B"
    },
    {
      question: "Question 4: Lorem ipsum dolor sit amet?",
      optionA: "Option A",
      optionB: "Option B"
    },
    {
      question: "Question 5: Lorem ipsum dolor sit amet?",
      optionA: "Option A",
      optionB: "Option B"
    },
    {
      question: "Question 6: Lorem ipsum dolor sit amet?",
      optionA: "Option A",
      optionB: "Option B"
    },
    {
      question: "Question 7: Lorem ipsum dolor sit amet?",
      optionA: "Option A",
      optionB: "Option B"
    },
    {
      question: "Question 8: Lorem ipsum dolor sit amet?",
      optionA: "Option A",
      optionB: "Option B"
    },
    {
      question: "Question 9: Lorem ipsum dolor sit amet?",
      optionA: "Option A",
      optionB: "Option B"
    },
    {
      question: "Question 10: Lorem ipsum dolor sit amet?",
      optionA: "Option A",
      optionB: "Option B"
    },
    {
      question: "Question 11: Lorem ipsum dolor sit amet?",
      optionA: "Option A",
      optionB: "Option B"
    },
    {
      question: "Question 12: Lorem ipsum dolor sit amet?",
      optionA: "Option A",
      optionB: "Option B"
    }
    // Add more questions here
  ];

  const displayNextQuestion = () => {
    setCurrentQuestionIndex(prevIndex => prevIndex + 1);
  };

  const displayPrevQuestion = () => {
    setCurrentQuestionIndex(prevIndex => prevIndex - 1);
  };

  return (
    <div className="container">
      <header>
        <h1>Personality Quiz</h1>
      </header>
      <main>
        {/* Start Page */}
        {currentQuestionIndex === 0 && (
          <section id="start-page" className="question-container">
            <h2>Welcome to the Personality Quiz!</h2>
            <img src="images/start.gif" alt="Start Image" />
            <p>Click the button below to start.</p>
            <button id="start-btn" onClick={displayNextQuestion}>Start Quiz</button>
          </section>
        )}

        {/* Question Pages */}
        {currentQuestionIndex > 0 && currentQuestionIndex <= questions.length && (
          <section className="question-container">
            <h2>{questions[currentQuestionIndex - 1].question}</h2>
            {currentQuestionIndex <= 2 && (
              <img
                src={`images/question${currentQuestionIndex}.gif`}
                alt={`Question Image ${currentQuestionIndex}`}
                onLoad={() =>
                  console.log(
                    `Loaded image with src: images/question${currentQuestionIndex}.gif and alt: Question Image ${currentQuestionIndex}`
                  )
                }
              />
            )}
            <div className="options">
              <button onClick={displayNextQuestion}>{questions[currentQuestionIndex - 1].optionA}</button>
              <button onClick={displayNextQuestion}>{questions[currentQuestionIndex - 1].optionB}</button>
            </div>
          </section>
        )}

        {/* Display end of quiz message */}
        {currentQuestionIndex > questions.length && (
          <section className="question-container">
            <h2>End of Quiz!</h2>
          </section>
        )}
      </main>
      <footer>
        <p>&copy; 2024 Personality Quiz</p>
      </footer>
    </div>
  );
}

export default App;
